type ZappButtonProps = {
  zappName: string;
  onClick: () => void;
  src: string;
};
export const ZappButton = ({
  zappName,
  onClick,
  src
}: ZappButtonProps): ReactElement => {
  const [loaded, setLoaded] = useState(false);
  console.log(src);
  return (
    <Container key={zappName} onClick={loaded ? onClick : undefined}>
      {!loaded && loadingComponent[zappName as keyof typeof loadingComponent]}
      <iframe
        onLoad={() => {
          setLoaded(true);
        }}
        style={{
          width: "100%",
          height: "100%"
        }}
        src={src}
      />
    </Container>
  );
};
